import { useState } from "react";
import logo from "../logo.svg";
import "../App.css";

const Batching = () => {
  const [isLoading, setLoading] = useState(false);
  const [data, setData] = useState({});

  const handleClick = (e) => {
    setLoading(true); // ‚è≥ Wait till the end to re-render
    setData({}); // ‚è≥ Wait till the end to re-render
    // üöÄ Now it re-render once, batching the previous state updated!
    console.log("pasa por el handleClick");
  };

  // useEffect(() => {
  //   setLoading(true); // ‚è≥ Wait till the end to re-render
  //   setData({}); // ‚è≥ Wait till the end to re-render
  //   // üöÄ Now it re-render once, batching the previous state updated!
  //   console.log('pasa por el useEffect');
  // }, [value]);

  return (
    <header className="App-header-test-page">
      <h1>React 18</h1>
      <div className="container-test">Automatic batching</div>
      <img src={logo} className="App-logo-test-page" alt="logo" />
      <div>
        <a href="/" className="App-link">
          Home
        </a>
      </div>
      <div className="container-test">
        <button onClick={handleClick}>Click me!</button>
      </div>
    </header>
  );
};

export default Batching;
