import { useEffect, useState, useTransition } from "react";
import logo from "../logo.svg";
import "../App.css";

const Transition = () => {
  const [value, setValue] = useState("");
  const [pokemon, setPokemon] = useState();
  const [loadingFetch, setLoadingFetch] = useState(false);
  const [errorFetch, setErrorFetch] = useState("");
  const [isLoading, startTransition] = useTransition();

  const handlerChange = (e) => {
    const valueInput = e.target.value;
    setValue(valueInput);

    // startTransition(() => {
    //   setLoadingFetch(true);
    // });
  };

  useEffect(() => {
    setLoadingFetch(true);
    setErrorFetch(false);

    fetch(`https://pokeapi.co/api/v2/pokemon/${value}`)
      .then((result) => result.json())
      .then((result) => {
        startTransition(() => {
          setPokemon([...result]);
        });
      })
      .catch((error) => {
        startTransition(() => {
          setErrorFetch(error);
        });
      });

    setLoadingFetch(false);
  }, [value]);

  useEffect(() => {
    console.log(pokemon);
  }, [pokemon]);

  return (
    <header className="App-header-test-page">
      <img src={logo} className="App-logo-test-page" alt="logo" />
      <div>
        <a href="/" className="App-link">
          Home
        </a>
      </div>
      <div className="container-test">
        <label>Busca tu pokemon favorito</label>
        <input onChange={handlerChange} className="input-test" />
        <div>
          {loadingFetch && <span>cargando ...</span>}
          {!loadingFetch && pokemon && <p>pokemon?.results.forms?.[0].name</p>}
          {errorFetch && <p>La búsqueda no coincide con ningún pokemon</p>}
        </div>
      </div>
    </header>
  );
};

export default Transition;
